{
	dp[0] = 1;
	int ans = 1;
	for (int i = 0 ; s[i] ; ++i){
		if (s[i] == 'm' || s[i] == 'w'){
			puts("0");
			return;
		}
		dp[i + 1] = dp[i];
		if (i && s[i] == s[i - 1] && (s[i] == 'u' || s[i] == 'n')){
			INC(dp[i + 1] , dp[i - 1]);
		}
		ans = dp[i + 1];
	}
	OT(ans);
}
